import React, { Component } from "react";
import Newsitems from "./Newsitems";
import Spinner from "./Spinner";

import InfiniteScroll from "react-infinite-scroll-component";


export default class News extends Component {
    
  static defaultProps={
    country:"in",
    category:"general"
  }
   capitalizeFirstLetter=(string)=> {
    return string.charAt(0).toUpperCase() + string.slice(1);
  }
  
  constructor(props){
      super(props)
      console.log("hello i am comstructor");
      this.state={
          articles:[],
          loading:false,
          page:1,
          totalResults:0
      }
      document.title=`${this.capitalizeFirstLetter(this.props.category)} NewsMonkey`
  }
  async componentDidMount(){
  
   let url= `https://newsapi.org/v2/top-headlines?country=${this.props.country}&category=${this.props.category}&apiKey=e76decae8b7847cd92b6ecf8894c2aea&page=1&pageSize=18`;
    this.setState({loading:true})
   let data=await fetch(url);
   let parsedata=await data.json();
   console.log(parsedata);
    this.setState({
      articles:parsedata.articles,
      totalResults:parsedata.totalResults,
      loading:false})

  }
  handleonprev= async()=>{
    console.log("clicked on previous");
    let url= `https://newsapi.org/v2/top-headlines?country=${this.props.country}&category=${this.props.category}&apiKey=e76decae8b7847cd92b6ecf8894c2aea&page=${this.state.page-1}&pageSize=18`;
    this.setState({loading:true})
   let data=await fetch(url);
   let parsedata=await data.json();
   console.log(parsedata);

    this.setState({
      page:this.state.page-1,
      articles:parsedata.articles,
      loading:false

    })
  }
  handleonnext= async()=>{
    console.log("clicked on next");
    if(this.state.page+1>Math.ceil(this.state.totalResults/19)){

    }
    else{
    let url= ` https://newsapi.org/v2/top-headlines?country=${this.props.country}&category=${this.props.category}&apiKey=e76decae8b7847cd92b6ecf8894c2aea&page=${this.state.page+1}&pageSize=18`;
      this.setState({loading:true})
   let data=await fetch(url);
   let parsedata=await data.json();
   console.log(parsedata);


    this.setState({
      page:this.state.page+1,
      articles:parsedata.articles,
      loading:false

    })
  }
}
// fetchMoreData = async() => {

// let url= `https://newsapi.org/v2/top-headlines?country=${this.props.country}&category=${this.props.category}&apiKey=e76decae8b7847cd92b6ecf8894c2aea&page=1&pageSize=18`;
// this.setState({loading:true})
// let data=await fetch(url);
// let parsedata=await data.json();
// console.log(parsedata);
// this.setState({
//   articles:this.state.articles.concat(parsedata.articles),
//   totalResults:parsedata.totalResults,
//   loading:false,
//   page:this.state.page+1})

// }
    render() {
     
    return (
        <>
        {/* {this.state.loading && <Spinner />} */}
        <h1 className="mx-3 my-3" style= {{textAlign: "center"}}>News-Monkey Top Headlines from {this.capitalizeFirstLetter(this.props.category)}</h1>
       
        
      <div className="container my-3">
      <InfiniteScroll
          dataLength={this.state.articles.length}
          next={this.fetchMoreData}
          hasMore={this.state.articles.length!==this.state.totalResults}
          loader={<Spinner/>}>
        <div className="row my-3">
        
        
            {!this.state.loading && this.state.articles.map((elements)=>{
               return <div className="col-md-4"key={elements.url}>
                    <Newsitems title={elements.title.slice(0,45)} published={elements.publishedAt} description={elements.content} urlToImage={elements.urlToImage} newsUrl={elements.url} source={elements.source.name} />
                </div>
                
            })}
           
            </div>
            </InfiniteScroll>
            </div>

                                        {/*  Code of button */}                  
         
         <div className="container d-flex justify-content-between my-3">
         <button disabled={this.state.page<=1} type="button" className="btn btn-dark" onClick={this.handleonprev}>Previous</button>
         <button type="button" className="btn btn-dark" onClick={this.handleonnext}>Next</button>
         </div>
         
        
        {/* <div className="row my-4">
        <div className="col-md-4">
          <Newsitems title="myTitle" description="myDesc" />
        </div>
        <div className="col-md-4">
          <Newsitems title="myTitle" description="myDesc" />
        </div>
        <div className="col-md-4">
          <Newsitems title="myTitle" description="myDesc" />
        </div>
        </div> */}
      
      </>
    );
  }
}
