import React, { Component } from "react";
import Newsitems from "./Newsitems";
import Spinner from "./Spinner";

export default class News extends Component {
    
  constructor(){
      super()
      console.log("hello i am comstructor");
      this.state={
          articles2:[],
          loading:true,
          page:1
      }
  }
  async componentDidMount(){
  
   let url= "https://newsapi.org/v2/top-headlines?country=in&category=business&apiKey=e76decae8b7847cd92b6ecf8894c2aea&page=1&pageSize=19";

   let data=await fetch(url);
   let parsedata=await data.json();
   console.log(parsedata);
   this.setState({articles2:parsedata.articles,totalResults:parsedata.totalResults})

  }
  handleonprev= async()=>{
    console.log("clicked on previous");
    let url= `https://newsapi.org/v2/top-headlines?country=in&category=business&apiKey=e76decae8b7847cd92b6ecf8894c2aea&page=${this.state.page-1}&pageSize=19`;

   let data=await fetch(url);
   let parsedata=await data.json();
   console.log(parsedata);

    this.setState({
      page:this.state.page-1,
      articles2:parsedata.articles

    })
  }
  handleonnext= async()=>{
    console.log("clicked on next");
    if(this.state.page+1>Math.ceil(this.state.totalResults/19)){

    }
    else{
    let url= ` https://newsapi.org/v2/top-headlines?country=in&category=business&apiKey=e76decae8b7847cd92b6ecf8894c2aea&page=${this.state.page+1}&pageSize=19`;

   let data=await fetch(url);
   let parsedata=await data.json();
   console.log(parsedata);

    this.setState({
      page:this.state.page+1,
      articles2:parsedata.articles

    })
  }
}
    render() {
     
    return (
        <>
        {this.state.loading && <Spinner />}
        <h1 className="mx-3 my-3" style= {{textAlign: "center"}}>News-Monkey Top Headlines</h1>
        
      <div className="container my-3">
        <div className="row my-3">
            {this.state.articles2.map((elements)=>{
               return <div className="col-md-4"key={elements.url}>
                    <Newsitems title={elements.title.slice(0,45)} description={elements.content} urlToImage={elements.urlToImage} newsUrl={elements.url}/>
                </div>
            })}
         <div className="container d-flex justify-content-between my-3">
         <button disabled={this.state.page<=1} type="button" className="btn btn-dark" onClick={this.handleonprev}>Previous</button>
         <button type="button" className="btn btn-dark" onClick={this.handleonnext}>Next</button>
         </div>
         
        </div>
        {/* <div className="row my-4">
        <div className="col-md-4">
          <Newsitems title="myTitle" description="myDesc" />
        </div>
        <div className="col-md-4">
          <Newsitems title="myTitle" description="myDesc" />
        </div>
        <div className="col-md-4">
          <Newsitems title="myTitle" description="myDesc" />
        </div>
        </div> */}
      </div>
      </>
    );
  }
}
