      this.state={
          articles2:[],
          loading:false,
          page:1
      }
  }
  async componentDidMount(){
  
   let url= "https://newsapi.org/v2/top-headlines?country=in&apiKey=e76decae8b7847cd92b6ecf8894c2aea&page=1";

   let data=await fetch(url);
   let parsedata=await data.json();
   console.log(parsedata);
   this.setState({articles2:parsedata.articles})

